    public static void initialize() {}
    
    private static ModFlowerPotBlock getFlowerPot(@Nullable Supplier<FlowerPotBlock> emptyPot, Supplier<Block> content, String name) {
        return new ModFlowerPotBlock(emptyPot, content, BlockBehaviour.Properties
        .ofFullCopy(Blocks.FLOWER_POT)
        .setId(ResourceKey.create(
            ForgeRegistries.BLOCKS.getRegistryKey(), 
            ModResourceLocation.create(name))));
    }
    
    private static <T extends Block> RegistryObject<T> registerFlowerPot(String name, Supplier<T> block) {
        RegistryObject<T> ret = registerNoItem(name, block);
        ModTabs.addToFunctionalBlocks(Registration.ITEMS.register(name, () -> new BlockItem(ret.get(), new Item.Properties()
        .setId(ResourceKey.create(
            ForgeRegistries.ITEMS.getRegistryKey(), 
            ModResourceLocation.create(name))))));
        return ret;
    }

    static class ModFlowerPotBlock extends FlowerPotBlock {
        public ModFlowerPotBlock(@Nullable Supplier<FlowerPotBlock> emptyPot, Supplier<? extends Block> content, Properties properties) {
            super(emptyPot, content, properties);
            if (emptyPot != null) {
                emptyPot.get().addPlant(ForgeRegistries.BLOCKS.getKey(content.get()), () -> this);
            }
        }
    }
